import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../../context/cartContext";
import { AuthContext } from "../../context/AuthContext";

const ItemDetail = ({ id, title, price, img, description, stock }) => {
  const { addItem } = useContext(CartContext);
  const { isAuthenticated } = useContext(AuthContext);
  const [quantityAdded, setQuantityAdded] = useState(0);

  const handleAddToCart = (quantity) => {
    setQuantityAdded(quantity);
    addItem({ id, title, price, img }, quantity);
  };

  return (
    <div className="item-detail">
      <h2>{title}</h2>
      <img src={img} alt={title} />
      <p>{description}</p>
      <p>Precio: ${price}</p>
      <p>Stock: {stock}</p>
      {isAuthenticated ? (
        quantityAdded ? (
          <Link to="/cart" className="btn btn-primary">Ir al Carrito</Link>
        ) : (
          <button onClick={() => handleAddToCart(1)} className="btn btn-primary">Aggregar al carrito</button>
        )
      ) : (
        <p>Inicia sesion para comprar.</p>
      )}
    </div>
  );
};

export default ItemDetail;
