import React, { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { onAuthStateChanged, signInWithPopup, signOut } from 'firebase/auth';
import { auth, googleProvider } from '../components/service/firebaseConfig';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const fetchUserRole = async (firebaseUser) => {
    try {
      const token = await firebaseUser.getIdToken();
      const response = await axios.post('http://localhost:3001/auth/verifyRole', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser({ ...firebaseUser, role: response.data.userRole });
    } catch (error) {
      console.error('Error al obtener el rol del usuario:', error);
      setUser(firebaseUser); // Si no puede obtener el rol, aún establecemos al usuario
    }
  };

  const loginWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const firebaseUser = result.user;
      await fetchUserRole(firebaseUser); // Llamar a la función para obtener el rol
      navigate(firebaseUser.role === 'admin' ? '/admin' : '/');
    } catch (error) {
      console.error('Error al iniciar sesión con Google:', error);
      setError(error);
    }
  };

  const logout = async () => {
    try {
      const firebaseUser = auth.currentUser;
      if (!firebaseUser) {
        navigate('/');
        return;
      }

      const token = await firebaseUser.getIdToken();

      await axios.post('http://localhost:3001/auth/logout', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      await signOut(auth);

      setUser(null);
      navigate('/');
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
      setError(error);
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      if (firebaseUser) {
        await fetchUserRole(firebaseUser);
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  return (
    <AuthContext.Provider value={{ user, loading, error, loginWithGoogle, logout }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
