import React, { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { onAuthStateChanged, signInWithPopup, signOut } from 'firebase/auth';
import { auth, googleProvider } from '../components/service/firebaseConfig';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const fetchUserRole = async () => {
    try {
      if (auth.currentUser) {
        const token = await auth.currentUser.getIdToken();
        const response = await axios.post('http://localhost:3001/auth/verifyRole', {}, {
          headers: { Authorization: `Bearer ${token}` },
        });
        // Actualizamos el objeto de usuario con el rol
        setUser((prev) => ({ ...auth.currentUser, role: response.data.userRole }));
      }
    } catch (error) {
      console.error('Error al obtener el rol del usuario:', error);
      setError(error);
    }
  };

  const loginWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const firebaseUser = result.user;

      const token = await firebaseUser.getIdToken();
      const response = await axios.post('http://localhost:3001/auth/verifyRole', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const userRole = response.data.userRole;
      console.log(`Usuario autenticado con rol: ${userRole}`);
      
      // Almacenar el objeto completo del usuario de Firebase con el rol adicional
      setUser({ ...firebaseUser, role: userRole });

      if (userRole === 'admin') {
        navigate('/admin');
      } else {
        navigate('/');
      }
    } catch (error) {
      console.error('Error al iniciar sesi贸n con Google:', error);
      setError(error);
    }
  };

  const logout = async () => {
    try {
      if (!auth.currentUser) {
        navigate('/');
        return;
      }

      const token = await auth.currentUser.getIdToken();

      await axios.post('http://localhost:3001/auth/logout', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      await signOut(auth);

      setUser(null);
      navigate('/');
    } catch (error) {
      console.error('Error al cerrar sesi贸n:', error);
      setError(error);
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      if (firebaseUser) {
        const token = await firebaseUser.getIdToken();
        try {
          const response = await axios.post('http://localhost:3001/auth/verifyRole', {}, {
            headers: { Authorization: `Bearer ${token}` },
          });
          setUser({ ...firebaseUser, role: response.data.userRole });
        } catch (error) {
          console.error('Error al obtener el rol del usuario:', error);
          setUser(null);
        }
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  return (
    <AuthContext.Provider value={{ user, loading, error, loginWithGoogle, logout }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
