import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { Table, Button, Spinner } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';

const ClientesList = () => {
  const [clientes, setClientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user, auth } = useAuth(); // Obtener auth también del contexto
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClientes = async () => {
      if (!auth || !auth.currentUser) {
        console.error('Usuario no autenticado o no disponible para obtener clientes.');
        setLoading(false); // Detener el spinner si no hay usuario autenticado
        return;
      }

      try {
        const token = await auth.currentUser.getIdToken();
        console.log('Token obtenido:', token);

        const response = await axios.get('http://localhost:3001/api/clients', {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log('Clientes obtenidos:', response.data);
        setClientes(response.data);
      } catch (error) {
        console.error('Error fetching clients:', error);
      } finally {
        setLoading(false);
      }
    };

    if (auth && auth.currentUser) {
      fetchClientes();
    } else {
      console.error('Usuario no autenticado o no disponible.');
      setLoading(false);
    }
  }, [auth]);

  const handleDeleteCliente = async (id) => {
    if (!auth || !auth.currentUser) {
      console.error('Usuario no autenticado o user es null.');
      return;
    }

    if (window.confirm('¿Estás seguro de que deseas eliminar este cliente?')) {
      setLoading(true);
      try {
        const token = await auth.currentUser.getIdToken();
        await axios.delete(`http://localhost:3001/api/clients/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Actualizar la lista después de eliminar
        setClientes((prevClientes) => prevClientes.filter((cliente) => cliente.id !== id));
      } catch (error) {
        console.error('Error deleting client:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  if (loading) {
    return (
      <div className="text-center mt-5">
        <Spinner animation="border" />
      </div>
    );
  }

  if (!auth || !auth.currentUser) {
    return (
      <div className="text-center mt-5">
        <p>Por favor, inicia sesión para ver la lista de clientes.</p>
        <Button variant="primary" onClick={() => navigate('/login')}>
          Iniciar sesión
        </Button>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h2>Lista de Clientes</h2>
      <Link to="/form/client" className="btn btn-primary mb-3">Nuevo Cliente</Link>
      {clientes.length === 0 ? (
        <p>No hay clientes disponibles.</p>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>CUIT</th>
              <th>Email</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map((cliente) => (
              <tr key={cliente.id}>
                <td>{cliente.id}</td>
                <td>{cliente.name}</td>
                <td>{cliente.cuit}</td>
                <td>{cliente.email}</td>
                <td>
                  <Link to={`/form/client/${cliente.id}`} className="btn btn-warning me-2">
                    Editar
                  </Link>
                  <Button variant="danger" onClick={() => handleDeleteCliente(cliente.id)}>
                    Eliminar
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default ClientesList;
