import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Alert from 'react-bootstrap/Alert';

const PurchaseForm = ({ supplierId }) => {
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [price, setPrice] = useState(0);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (supplierId) {
      fetchSupplier(supplierId);
    }
  }, [supplierId]);

  const fetchSupplier = async (id) => {
    try {
      const supplierResponse = await axios.get(`/suppliers/${id}`);
      setSupplier(supplierResponse.data);
      // Fetch products of this supplier's category
      const productsResponse = await axios.get(`/products/by-category?category=${supplierResponse.data.category}`);
      setProducts(productsResponse.data);
    } catch (err) {
      console.error('Error fetching supplier or products:', err);
      setError('No se pudo obtener la información del proveedor o productos');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validar que los valores numéricos sean válidos
    const quantityValue = parseFloat(quantity);
    const priceValue = parseFloat(price);

    if (isNaN(quantityValue) || quantityValue <= 0) {
      setError('La cantidad debe ser un número mayor a 0');
      return;
    }

    if (isNaN(priceValue) || priceValue <= 0) {
      setError('El precio debe ser un número mayor a 0');
      return;
    }

    const total = quantityValue * priceValue;

    // Asegúrate de enviar un total válido
    if (isNaN(total) || total <= 0) {
      setError('El total debe ser un número mayor a 0');
      return;
    }

    try {
      const purchaseData = {
        supplierId,
        productId: selectedProduct?.id || null,
        quantity: quantityValue,
        price: priceValue,
        total,
        type: 'compra',
        buyer_info: JSON.stringify({ supplierId })
      };

      const response = await axios.post('/purchases', purchaseData);

      if (response.data) {
        navigate('/purchase-report');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Error al registrar la compra. Por favor, inténtelo de nuevo.');
    }
  };

  const handleQuantityChange = (e) => {
    const value = e.target.value;
    setQuantity(value);
    setError(''); // Limpiar errores cuando se actualiza el campo
  };

  const handlePriceChange = (e) => {
    const value = e.target.value;
    setPrice(value);
    setError(''); // Limpiar errores cuando se actualiza el campo
  };

  const handleProductChange = (e) => {
    const product = products.find((p) => p.id === parseInt(e.target.value));
    setSelectedProduct(product);
    if (product) {
      setPrice(product.price);
    } else {
      setPrice(0);
    }
    setError(''); // Limpiar errores cuando se selecciona un nuevo producto
  };

  return (
    <Card className="p-4 mx-auto my-4" style={{ maxWidth: '600px' }}>
      <Card.Body>
        <Card.Title>Nueva Compra</Card.Title>
        {supplier && <Card.Subtitle className="mb-4">Proveedor: {supplier.name}</Card.Subtitle>}
        {error && <Alert variant="danger">{error}</Alert>}

        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Producto:</Form.Label>
            <Form.Select
              value={selectedProduct?.id || ''}
              onChange={handleProductChange}
            >
              <option value="">Selecciona un Producto Existente</option>
              {products.map((product) => (
                <option key={product.id} value={product.id}>
                  {product.title}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Cantidad:</Form.Label>
            <Form.Control
              type="number"
              min="1"
              value={quantity}
              onChange={handleQuantityChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Precio:</Form.Label>
            <Form.Control
              type="number"
              min="0"
              value={price}
              onChange={handlePriceChange}
              disabled={!!selectedProduct}
            />
          </Form.Group>

          <div className="d-flex justify-content-between">
            <Button variant="primary" type="submit">
              Guardar Compra
            </Button>
            <Button
              variant="secondary"
              onClick={() => navigate(`/form/product/new?supplierId=${supplierId}`)}
            >
              Agregar Nuevo Producto
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default PurchaseForm;
