import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Alert from 'react-bootstrap/Alert';

const PurchaseForm = ({ supplierId, onSave }) => {
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);
  const [newProductName, setNewProductName] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (supplierId) {
      fetchSupplier(supplierId);
    }
  }, [supplierId]);

  const fetchSupplier = async (id) => {
    try {
      const supplierResponse = await axios.get(`/suppliers/${id}`);
      setSupplier(supplierResponse.data);
      // Fetch products of this supplier's category
      const productsResponse = await axios.get(`/products?category=${supplierResponse.data.category}`);
      setProducts(productsResponse.data);
    } catch (err) {
      console.error('Error fetching supplier or products:', err);
      setError('No se pudo obtener la informaciÃ³n del proveedor o productos');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedProduct && !newProductName) {
      setError('Debe seleccionar un producto existente o agregar un nuevo producto con su nombre.');
      return;
    }

    onSave({
      supplierId,
      productId: selectedProduct ? selectedProduct.id : null,
      newProductName: selectedProduct ? null : newProductName,
      quantity,
      price,
    });
  };

  return (
    <Card className="p-4 mx-auto my-4" style={{ maxWidth: '600px' }}>
      <Card.Body>
        <Card.Title>Nueva Compra</Card.Title>
        {supplier && <Card.Subtitle className="mb-4">Proveedor: {supplier.name}</Card.Subtitle>}
        {error && <Alert variant="danger">{error}</Alert>}
        
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Producto:</Form.Label>
            <Form.Select
              value={selectedProduct || ''}
              onChange={(e) => {
                const product = products.find((p) => p.id === parseInt(e.target.value));
                setSelectedProduct(product);
                setPrice(product ? product.price : 0);
              }}
            >
              <option value="">Selecciona un Producto Existente</option>
              {products.map((product) => (
                <option key={product.id} value={product.id}>
                  {product.title}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>O Nombre del Producto Nuevo:</Form.Label>
            <Form.Control
              type="text"
              value={newProductName}
              onChange={(e) => setNewProductName(e.target.value)}
              placeholder="Escribe el nombre del producto nuevo"
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Cantidad:</Form.Label>
            <Form.Control
              type="number"
              min="1"
              value={quantity}
              onChange={(e) => setQuantity(parseInt(e.target.value))}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Precio:</Form.Label>
            <Form.Control
              type="number"
              min="0"
              value={price}
              onChange={(e) => setPrice(parseFloat(e.target.value))}
              disabled={!!selectedProduct}
            />
          </Form.Group>

          <div className="d-flex justify-content-between">
            <Button variant="primary" type="submit">
              Guardar Compra
            </Button>
            <Button
              variant="secondary"
              onClick={() => navigate(`/form/product/new?supplierId=${supplierId}`)}
            >
              Agregar Nuevo Producto
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default PurchaseForm;
