import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Button, Table, Alert } from 'react-bootstrap';

const SupplierList = () => {
  const [suppliers, setSuppliers] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchSuppliers();
  }, []);

  const fetchSuppliers = async () => {
    try {
      const response = await axios.get('/suppliers'); // Corregir la URL
      setSuppliers(response.data);
    } catch (error) {
      console.error('Error fetching suppliers:', error);
      setError('Error al obtener la lista de proveedores');
    }
  };

  const handleDeleteSupplier = async (id) => {
    const isConfirmed = window.confirm('¿Estás seguro de que deseas eliminar este proveedor?');
    if (!isConfirmed) return;

    try {
      await axios.delete(`/api/suppliers/${id}`);
      fetchSuppliers(); // Refrescar la lista de proveedores
    } catch (error) {
      console.error('Error deleting supplier:', error);
      setError('Error al eliminar el proveedor');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Lista de Proveedores</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Contacto</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {suppliers.map((supplier) => (
            <tr key={supplier.id}>
              <td>{supplier.name}</td>
              <td>{supplier.contact}</td>
              <td>
                <Button
                  variant="warning"
                  onClick={() => navigate(`/form/supplier/${supplier.id}`)}
                >
                  Editar
                </Button>
                {' '}
                <Button
                  variant="danger"
                  onClick={() => handleDeleteSupplier(supplier.id)}
                >
                  Eliminar
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default SupplierList;
