const ClientForm = ({ initialData = {}, onSave }) => {
  const [client, setClient] = useState(initialData);
  const [loading, setLoading] = useState(false);
  const { id } = useParams(); // Para obtener el ID si estamos editando un cliente existente
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      // Si hay un ID, estamos en modo edición: cargar datos del cliente
      fetchClientById(id);
    }
  }, [id]);

  const fetchClientById = async (clientId) => {
    try {
      setLoading(true);
      const response = await axios.get(`/clients/${clientId}`);
      setClient(response.data);
    } catch (error) {
      console.error('Error al obtener el cliente:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setClient((prevClient) => ({
      ...prevClient,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (id) {
        // Actualización de cliente existente
        const response = await axios.put(`/clients/${id}`, client);
        console.log('Cliente actualizado:', response.data);
        onSave && onSave(response.data);
      } else {
        // Creación de nuevo cliente
        const response = await axios.post('/clients', client);
        console.log('Cliente creado:', response.data);
        onSave && onSave(response.data);
      }
      navigate('/clients'); // Navegar de vuelta a la lista de clientes
    } catch (error) {
      console.error('Error al agregar o actualizar el cliente:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5">
      <div className="card">
        <div className="card-header">
          <h2>{id ? 'Editar Cliente' : 'Agregar Cliente'}</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Nombre del Cliente:</label>
              <input
                type="text"
                id="name"
                name="name"
                className="form-control"
                value={client.name || ''}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="cuit" className="form-label">CUIT:</label>
              <input
                type="text"
                id="cuit"
                name="cuit"
                className="form-control"
                value={client.cuit || ''}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Correo Electrónico:</label>
              <input
                type="email"
                id="email"
                name="email"
                className="form-control"
                value={client.email || ''}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? 'Guardando...' : id ? 'Actualizar Cliente' : 'Agregar Cliente'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ClientForm;
