import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import FormText from '../FormText/FormText';<Form onSubmit={handleSubmit}>
<FormText
  name="name"
  value={supplier.name}
  onChange={handleChange}
  placeholder="Nombre"
  required
  autoComplete="name" // Example: 'name' for person's full name
/>
<FormText
  name="phone"
  value={supplier.phone}
  onChange={handleChange}
  placeholder="Teléfono"
  required
  autoComplete="tel" // Example: 'tel' for telephone number
/>
<FormText
  name="email"
  value={supplier.email}
  onChange={handleChange}
  placeholder="Email"
  required
  autoComplete="email" // Example: 'email' for email address
/>
<FormSelect
  name="category"
  value={supplier.category}
  onChange={handleChange}
  placeholder="Categoría"
  required
  autoComplete="category" // Example: 'category' for product category
  optionGroups={[
    {
      title: 'Categoría',
      options: [
        { value: 'Notebook', label: 'Notebook' },
        { value: 'Celular', label: 'Celular' },
        { value: 'Tablet', label: 'Tablet' },
      ],
    },
  ]}
  className='mx-2'
/>
<Button>Guardar</Button>
</Form>


const SupplierForm = ({ initialData = {}, onSave }) => {
  const [supplier, setSupplier] = useState({
    name: '',
    phone: '',
    email: '',
    category: '',
    ...initialData,
  });

  useEffect(() => {
    setSupplier((prevSupplier) => ({ ...prevSupplier, ...initialData }));
  }, [initialData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSupplier((prevSupplier) => ({ ...prevSupplier, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(supplier);
    // Limpiar el formulario después de guardar
    setSupplier({ name: '', phone: '', email: '', category: '' });
  };

  return (
    <Form onSubmit={handleSubmit}>
  <FormText
    name="name"
    value={supplier.name}
    onChange={handleChange}
    placeholder="Nombre"
    required
    autoComplete="name" // Example: 'name' for person's full name
  />
  <FormText
    name="phone"
    value={supplier.phone}
    onChange={handleChange}
    placeholder="Teléfono"
    required
    autoComplete="tel" // Example: 'tel' for telephone number
  />
  <FormText
    name="email"
    value={supplier.email}
    onChange={handleChange}
    placeholder="Email"
    required
    autoComplete="email" // Example: 'email' for email address
  />
  <FormSelect
    name="category"
    value={supplier.category}
    onChange={handleChange}
    placeholder="Categoría"
    required
    autoComplete="category" // Example: 'category' for product category
    optionGroups={[
      {
        title: 'Categoría',
        options: [
          { value: 'Notebook', label: 'Notebook' },
          { value: 'Celular', label: 'Celular' },
          { value: 'Tablet', label: 'Tablet' },
        ],
      },
    ]}
    className='mx-2'
  />
  <Button>Guardar</Button>
</Form>

  );
};

export default SupplierForm;
