import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import FormGroup from 'react-bootstrap/esm/FormGroup';
import { getProductById } from '../../asyncMock';

const EditItemForm = ({ onSave }) => {
    const { itemId } = useParams();
    const [product, setProduct] = useState(null);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [stock, setStock] = useState('');
    const [price, setPrice] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        getProductById(itemId).then((product) => {
            if (product) {
                setProduct(product);
                setTitle(product.title || '');
                setDescription(product.description || '');
                setStock(product.stock || '');
                setPrice(product.price || '');
            }
        });
    }, [itemId]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const updatedProduct = { ...product, title, description, stock, price };
        onSave(updatedProduct);
        navigate('/');
    };

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <Form className='container' onSubmit={handleSubmit}>
            <Row className='justify-content-center col-12'>
                <FormGroup className='col-6'>
                    <Form.Label htmlFor="title">Nombre</Form.Label>
                    <Form.Control 
                        type="text" 
                        id="title" 
                        name="title" 
                        value={title} 
                        onChange={(e) => setTitle(e.target.value)} 
                    />
                </FormGroup>
            </Row>
            <Row className='justify-content-center'>
                <FormGroup className='col-8'>
                    <Form.Label htmlFor="description">Descripci√≥n</Form.Label>
                    <Form.Control 
                        type='text' 
                        id="description" 
                        name="description" 
                        value={description} 
                        onChange={(e) => setDescription(e.target.value)} 
                    />
                </FormGroup>
            </Row>
            <Row className='justify-content-center my-3'>
                <FormGroup className='col-4'>
                    <Form.Label htmlFor="stock">Stock</Form.Label>
                    <Form.Control 
                        type="text" 
                        id="stock" 
                        name="stock" 
                        value={stock} 
                        onChange={(e) => setStock(e.target.value)} 
                    />
                </FormGroup>
                <FormGroup className='col-4'>
                    <Form.Label htmlFor="price">Precio</Form.Label>
                    <Form.Control 
                        type="text" 
                        id="price" 
                        name="price" 
                        value={price} 
                        onChange={(e) => setPrice(e.target.value)} 
                    />
                </FormGroup>
            </Row>
            <Button type="submit">Guardar Cambios</Button>
        </Form>
    );
};

export default EditItemForm;
