import React, { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { onAuthStateChanged, signInWithPopup, signOut } from 'firebase/auth';
import { auth, googleProvider } from '../components/service/firebaseConfig';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const fetchUserRole = async (firebaseUser) => {
    try {
      if (firebaseUser) {
        const token = await firebaseUser.getIdToken();
        const response = await axios.post('http://localhost:3001/auth/verifyRole', {}, {
          headers: { Authorization: `Bearer ${token}` },
        });
        return { ...firebaseUser, role: response.data.userRole };
      }
      return null;
    } catch (error) {
      console.error('Error al obtener el rol del usuario:', error);
      setError(error);
      return null;
    }
  };

  const loginWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const firebaseUser = result.user;
      const userWithRole = await fetchUserRole(firebaseUser);

      setUser(userWithRole);
      navigate('/');
    } catch (error) {
      console.error('Error al iniciar sesi贸n con Google:', error);
      setError(error);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      navigate('/');
    } catch (error) {
      console.error('Error al cerrar sesi贸n:', error);
      setError(error);
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      if (firebaseUser) {
        const userWithRole = await fetchUserRole(firebaseUser);
        setUser(userWithRole);
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  return (
    <AuthContext.Provider value={{ user, loading, error, loginWithGoogle, logout }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
