import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

const Profile = ({ user }) => {
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    cuit: '', 
  });

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/profiles/${user.uid}`);
        setProfile(response.data);
      } catch (error) {
        console.error("Error fetching profile data:", error);
      }
    };

    if (user && user.uid) {
      fetchProfile();
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:3001/api/profiles/${user.uid}`, profile);
      alert('Perfil actualizado correctamente');
    } catch (error) {
      console.error("Error actualizando el perfil:", error);
      alert('Hubo un error al actualizar el perfil');
    }
  };

  return (
    <Form onSubmit={handleSubmit} className="container mt-4 p-4 border rounded">
      <h2>Perfil del Cliente</h2>
      <Form.Group className="mb-3" controlId="name">
        <Form.Label>Nombre</Form.Label>
        <Form.Control
          type="text"
          name="name"
          value={profile.name}
          onChange={handleChange}
          required
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="email">
        <Form.Label>Email</Form.Label>
        <Form.Control
          type="email"
          name="email"
          value={profile.email}
          onChange={handleChange}
          readOnly
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="cuit">
        <Form.Label>CUIT</Form.Label>
        <Form.Control
          type="text"
          name="cuit"
          value={profile.cuit}
          onChange={handleChange}
        />
      </Form.Group>
      <Button variant="primary" type="submit">Actualizar Perfil</Button>
    </Form>
  );
};

export default Profile;
