import React, { useEffect, useState } from 'react';
import ItemList from "../ItemList/ItemList";
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ItemListContainer = ({ greeting }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const { categoryId } = useParams();

    useEffect(() => {
        setLoading(true);
        
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:3000/api/productos');
                let productsData = response.data;

                if (categoryId) {
                    productsData = productsData.filter((product) => product.category === categoryId);
                }

                console.log("Productos obtenidos:", productsData); 
                setProducts(productsData);
            } catch (error) {
                console.error("Error al obtener los productos:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, [categoryId]);

    return (
        <div className="fw-bolder">
            <h1 className="rounded-5 text-uppercase bd-blue-600">{greeting}</h1>
            <div className="text-dark-emphasis fw-bolder">
                {loading ? (
                    <div>...loading</div>
                ) : (
                    <ItemList products={products} />
                )}
            </div>
        </div>
    );
};

export default ItemListContainer;



