import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext'; 
import { Table, Button, Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const ClientesList = () => {
  const [clientes, setClientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const { auth } = useAuth(); // Obtener `auth` directamente del contexto

  useEffect(() => {
    const fetchClientes = async () => {
      try {
        if (auth && auth.currentUser) { // Validar si `auth` y `auth.currentUser` existen
          const token = await auth.currentUser.getIdToken();
          console.log('Token obtenido:', token); // Para verificar si obtenemos el token correctamente

          // Realizar la solicitud para obtener los clientes
          const response = await axios.get('http://localhost:3001/api/clients', {
            headers: { Authorization: `Bearer ${token}` }
          });

          console.log('Respuesta de la API:', response.data); // Log para verificar la respuesta

          // Verificar si hay datos y luego establecer el estado
          if (response.data && Array.isArray(response.data)) {
            setClientes(response.data);
          } else {
            console.error('La respuesta de la API no tiene el formato esperado:', response.data);
          }
        } else {
          console.error("Usuario no autenticado o `auth.currentUser` es null.");
        }
      } catch (error) {
        console.error('Error fetching clients:', error);
      } finally {
        setLoading(false);
      }
    };

    if (auth && auth.currentUser) {
      fetchClientes();
    } else {
      setLoading(false);
    }
  }, [auth]);

  const handleDeleteCliente = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este cliente?')) {
      setLoading(true);
      try {
        if (auth && auth.currentUser) {
          const token = await auth.currentUser.getIdToken();
          await axios.delete(`http://localhost:3001/api/clients/${id}`, {
            headers: { Authorization: `Bearer ${token}` }
          });
          // Actualizar la lista después de eliminar
          setClientes((prevClientes) => prevClientes.filter(cliente => cliente.id !== id));
        } else {
          console.error("Error: no se puede eliminar el cliente porque el usuario no está autenticado.");
        }
      } catch (error) {
        console.error('Error deleting client:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  if (loading) {
    return <Spinner animation="border" />;
  }

  return (
    <div className="container mt-4">
      <h2>Lista de Clientes</h2>
      <Link to="/form/client" className="btn btn-primary mb-3">Nuevo Cliente</Link>
      {clientes.length === 0 ? (
        <p>No hay clientes disponibles.</p>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Email</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map(cliente => (
              <tr key={cliente.id}>
                <td>{cliente.id}</td>
                <td>{cliente.name}</td>
                <td>{cliente.email}</td>
                <td>
                  <Link to={`/form/client/${cliente.id}`} className="btn btn-warning me-2">
                    Editar
                  </Link>
                  <Button variant="danger" onClick={() => handleDeleteCliente(cliente.id)}>
                    Eliminar
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default ClientesList;
