import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import FormSelect from 'react-bootstrap/esm/FormSelect';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import FormGroup from 'react-bootstrap/esm/FormGroup';
import { getProductById } from '../../asyncmock';

const EditItemForm = ({ onSave }) => {
    const { itemId } = useParams();
    const [item, setItem] = useState(null);

    useEffect(() => {
        getProductById(itemId).then((data) => setItem(data));
    }, [itemId]);

    const [title, setTitle] = useState('');
    const [category, setCategory] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [stock, setStock] = useState('');

    useEffect(() => {
        if (item) {
            setTitle(item.title);
            setCategory(item.category);
            setPrice(item.price);
            setDescription(item.description);
            setStock(item.stock);
        }
    }, [item]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const updatedProduct = { ...item, title, category, price, description, stock };
        onSave(updatedProduct);
    };

    if (!item) return <div>Loading...</div>;

    return (
        <Form onSubmit={handleSubmit}>
            <Row className='justify-content-center'>
                <FormGroup>
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Form.Label>Categoría</Form.Label>
                    <FormSelect
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        autoComplete='category'
                        required
                    >
                        <option value="">Seleccionar categoría</option>
                        <option value="Notebook">Notebook</option>
                        <option value="Celular">Celular</option>
                        <option value="Tablet">Tablet</option>
                    </FormSelect>
                </FormGroup>
            </Row>
            <Row className='justify-content-center'>
                <FormGroup>
                    <Form.Label>Descripción</Form.Label>
                    <Form.Control type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
                </FormGroup>
            </Row>
            <Row className='justify-content-center'>
                <FormGroup>
                    <Form.Label>Stock</Form.Label>
                    <Form.Control type="text" value={stock} onChange={(e) => setStock(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Form.Label>Precio</Form.Label>
                    <Form.Control type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
                </FormGroup>
            </Row>
            <Button type="submit">Guardar Cambios</Button>
        </Form>
    );
};

export default EditItemForm;
