import "../App.css";
import { useState, useContext, useEffect } from "react";
import { CartContext } from "../context/cartContext";
import CheckoutForm from "../components/CheckoutForm/CheckoutForm";
import mockSalesData from "../mockSalesData"; // Asegúrate de importar los datos mock

const CheckOut = () => {
  const [loading, setLoading] = useState(false);
  const [orderId, setOrderId] = useState("");
  const [formData, setFormData] = useState({ name: "", phone: "", email: "" });
  const { cart, total, clearCart } = useContext(CartContext);

  useEffect(() => {
    // Obtener el último número de orden de mockSalesData y localStorage
    const orders = JSON.parse(localStorage.getItem('orders')) || [];
    const lastOrderMock = mockSalesData.reduce((max, order) => order.orderNumber > max ? order.orderNumber : max, 0);
    const lastOrderStorage = orders.reduce((max, order) => {
      const orderNum = order.orderNumber ? parseInt(order.orderNumber.split('_')[1]) : 0;
      return orderNum > max ? orderNum : max;
    }, lastOrderMock);

    setOrderId(`order_${lastOrderStorage + 1}`);
  }, []);

  const createOrder = ({ name, phone, email }) => {
    setLoading(true);
    const newOrderId = `order_${parseInt(orderId.split('_')[1])}`;

    try {
      const objOrder = {
        buyer: {
          name,
          phone,
          email,
        },
        items: cart,
        total: total,
        date: new Date().toISOString(),
        orderNumber: newOrderId
      };

      // Guardar orden en localStorage
      const orders = JSON.parse(localStorage.getItem('orders')) || [];
      orders.push(objOrder);
      localStorage.setItem('orders', JSON.stringify(orders));

      // Actualizar el número de orden para la próxima compra
      setOrderId(`order_${parseInt(orderId.split('_')[1]) + 1}`);
      clearCart();
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  if (loading) {
    return <h1>Se está generando su orden...</h1>;
  }

  if (orderId) {
    return <h1>El ID de su orden es: {orderId}</h1>;
  }

  return (
    <div>
      <div className="">
        <h3>Resumen de tu compra</h3>
        {cart.map(({ id, img, title, price, quantity }) => (
          <div key={id}>
            <p className="text-uppercase fw-bolder">{title}</p>
            <p>Precio Unitario: ${price}</p>
            <p>
              <img width={"80px"} src={img} alt="" />
            </p>
            <p>Cantidad: {quantity}</p>
          </div>
        ))}
        <h4>Total a pagar: ${total}</h4>
      </div>
      <h3>Checkout</h3>
      <CheckoutForm
        onConfirm={createOrder}
        formData={formData}
        onInputChange={handleInputChange}
      />
    </div>
  );
};

export default CheckOut;
