import React, { useState } from 'react';
import axios from '../service/axiosConfig';

const ProductForm = ({ initialData = {}, onSave }) => {
  const [product, setProduct] = useState(initialData);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (!product.id) {
        // Nueva inserción
        const response = await axios.post('/api/products', product); // Cambia el endpoint según tu API
        console.log('Producto agregado:', response.data);
        onSave(response.data); // Llama a la función de onSave con el producto agregado
      } else {
        // Actualización de producto
        const response = await axios.put(`/api/products/${product.id}`, product);
        console.log('Producto actualizado:', response.data);
        onSave(response.data);
      }
    } catch (error) {
      console.error('Error al agregar o actualizar el producto:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="product-form">
      <div className="form-group">
        <label htmlFor="title">Nombre del Producto:</label>
        <input
          type="text"
          id="title"
          name="title"
          value={product.title || ''}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="description">Descripción:</label>
        <textarea
          id="description"
          name="description"
          value={product.description || ''}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="price">Precio:</label>
        <input
          type="number"
          id="price"
          name="price"
          value={product.price || ''}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="purchasePrice">Precio de Compra:</label>
        <input
          type="number"
          id="purchasePrice"
          name="purchasePrice"
          value={product.purchasePrice || ''}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="img">URL de Imagen:</label>
        <input
          type="text"
          id="img"
          name="img"
          value={product.img || ''}
          onChange={handleChange}
        />
      </div>
      <button type="submit" className="btn btn-primary" disabled={loading}>
        {loading ? 'Guardando...' : 'Guardar Producto'}
      </button>
    </form>
  );
};

export default ProductForm;
