import "../App.css";
import { useState, useContext, useEffect } from "react";
import { CartContext } from "../context/cartContext";
import { collection, addDoc, doc, updateDoc } from 'firebase/firestore';
import { db } from '../components/service/firebaseConfig';
import { useAuth } from '../context/AuthContext'; 

const CheckOut = () => {
  const [loading, setLoading] = useState(false);
  const [orderId, setOrderId] = useState("");
  const { cart, total, clearCart } = useContext(CartContext);
  const { user } = useAuth();

  useEffect(() => {
    if (!user) {
      alert("Debe iniciar sesión para completar la compra.");
    }
  }, [user]);

  const createOrder = async () => {
    if (!user) return;

    setLoading(true);

    const orderData = {
      buyer: {
        uid: user.uid,
        name: user.displayName || "Nombre desconocido",
        email: user.email,
        phone: user.phoneNumber || "Teléfono no proporcionado"
      },
      items: cart,
      total: total,
      date: new Date().toISOString(),
    };

    try {
      // Crear la orden en Firestore
      const ordersCollectionRef = collection(db, 'orders');
      const docRef = await addDoc(ordersCollectionRef, orderData);
      setOrderId(docRef.id);

      // Actualizar el stock en Datastore para cada producto en el carrito
      for (const product of cart) {
        const productRef = doc(db, 'products', product.id); // Referencia al producto en Firestore
        const newStock = product.stock - product.quantity; // Calcular el nuevo stock

        if (newStock < 0) {
          console.error(`Stock insuficiente para el producto ${product.title}`);
          alert(`Stock insuficiente para el producto ${product.title}`);
          setLoading(false);
          return;
        }

        await updateDoc(productRef, { stock: newStock }); // Actualizar el stock en Firestore
      }

      // Limpiar el carrito solo si se completa todo el proceso
      clearCart();
    } catch (error) {
      console.log("Error al crear la orden o actualizar el stock:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (cart.length > 0) {
      createOrder();
    }
  }, [cart]);

  if (loading) {
    return <h1>Se está generando su orden...</h1>;
  }

  if (orderId) {
    return <h1>El ID de su orden es: {orderId}</h1>;
  }

  return (
    <div>
      <h3>Resumen de tu compra</h3>
      {cart.map(({ id, img, title, price, quantity }) => (
        <div key={id}>
          <p className="text-uppercase fw-bolder" id="itemName">
            {title}
          </p>
          <p>Precio Unitario: ${price}</p>
          <p>
            <img width={"80px"} src={img} alt="" />
          </p>
          <p>Cantidad: {quantity}</p>
        </div>
      ))}
      <h4>Total a pagar: ${total}</h4>
    </div>
  );
};

export default CheckOut;
